 INSERT INTO books (name,publishinghouse,author,year,booktype,description,graphic)VALUES('Dictionnaire du francais. Robert & Cle','None','praca zbiorowa',' 2013','Nauka języków','Nowoczesny słownik współczesny, przeznaczony dla studentów, dla których język francuski jest językiem obcym. Słownik opisuje słowa w kontekście współczesnej komunikacji; 22.000 haseł, wszystkie opatrzone bogatymi opisami (5.000 słów)....','https://skupszop.pl/images/books/9782090339994.jpg');
