 INSERT INTO books (name,publishinghouse,author,year,booktype,description,graphic)VALUES('Słownik szkolny. Chemia','None','Mirosław Grębosz',' 2008','Podręczniki szkolne, edukacja','Słownik zawiera ponad 3000 haseł, które obejmują wszystkie podstawowe działy chemii w zakresie często wykraczającym poza programy szkolne. Napisany jest językiem zrozumiałym dla ucznia, ale też tak, aby hasła nie traciły na ścisłości naukowej. Uwzględniona została w nim również gimnazjalna i licealna podstawa programowa oraz zalecenia Międzynarodowej Unii Chemii Czystej i Stosowanej, Komisji Nomenklaturowych Polskiego Towarzystwa Chemicznego oraz treści, które pojawiają się na olimpiadach chemicznych.','https://skupszop.pl/images/books/9788374356985.jpg');
 INSERT INTO books (name,publishinghouse,author,year,booktype,description,graphic)VALUES('Testy ortograficzne do uzupełniania','None','Mameła Maria',' 2013','Podręczniki szkolne, edukacja','Autorskie testy doświadczonej nauczycielki języka polskiego:- uwzględniają różny stopień trudności;- dotyczą licznych dziedzin nauki: historii, geografii, kultury, sztuki;- przeznaczone do dyktowania lub samodzielnego przepisywania;- przydatne uczniom, rodzicom i nauczycielom.','https://skupszop.pl/images/books/9788360220047.jpg');
 INSERT INTO books (name,publishinghouse,author,year,booktype,description,graphic)VALUES('Matematyka z wesołym Kangurem Poziom Junior i Kadet, niebieska','None','Zbigniew Bobiński',' 2009','Podręczniki szkolne, edukacja','W książce zamieszczono zadania z lat 1991-2008 konkursu "Kangur Matematyczny" w grupach wiekowych Kadet i Junior z pełnymi rozwiązaniami lub szkicami rozwiązań wybranych zadań....','https://skupszop.pl/images/books/9788360689189.jpg');
 INSERT INTO books (name,publishinghouse,author,year,booktype,description,graphic)VALUES('Biologia wybór testów MEDYK','None','praca zbiorowa',' 2009','Podręczniki szkolne, edukacja','Nowe jednotomowe wydanie Wyboru Testóww książce zawarto:3.500 pytań zamkniętych i otwartych!Pytania maturalne od roku 2002!','https://skupszop.pl/images/books/9788389745835.jpg');
