 INSERT INTO books (name,publishinghouse,author,year,booktype,description,graphic)VALUES('Przedwiośnie. Lektura z opracowaniem','None','Stefan Żeromski',' 2012','Podręczniki szkolne, edukacja','"Przedwiośnie" Stefana Żeromskiego to lektura z sugerowanego przez nową podstawę programową spisu utworów literackich do omawiania w liceum. Nasze wydanie zawiera tekst utworu oraz omówienie.','https://skupszop.pl/images/books/9788377383612.jpg');
 INSERT INTO books (name,publishinghouse,author,year,booktype,description,graphic)VALUES('Szkolna encyklopedia. Collins - John Farndon - ','None','John Farndon',' 2013','Podręczniki szkolne, edukacja','Szkolna encyklopedia została tak przygotowana, by mogła pomóc w szybkim wyszukiwaniu informacji. Może także służyć jako dodatkowe źródło wiedzy, gdy korzysta się z innych prac. Zawiera informacje z podstawowych przedmiotów nauczania, takich jak: historia, biologia, geografia, chemia - przedstawia również fakty z historii sztuki i literatury. Informuje o odkryciach, wynalazkach, wydarzeniach, faktach politycznych i sławnych postaciach. Publikacja ta może być wykorzystywana do samodzielnej pracy w domu i jako materiał pomocniczy w szkole.','https://skupszop.pl/images/books/9788327407191.jpg');
 INSERT INTO books (name,publishinghouse,author,year,booktype,description,graphic)VALUES('Repetytorium Gimnazjalisty biologia','None','Anna Mucha',' 2015','Podręczniki szkolne, edukacja','Repetytorium gimnazjalisty. Biologia to książka dostosowana do nowej podstawy programowej. Zawiera ona wszystkie zagadnienia potrzebne uczniowi każdej klasy gimnazjum. Napisana jest przystępnym, zrozumiałym językiem, a dla dodatkowego ułatwienia treść została w wielu miejscach ujęta w łatwe do zapamiętania schematy, wykresy i tabele. Najważniejsze pojęcia dokładnie wyjaśniono i ujęto w wyraźne ramki, dzięki którym można je szybko odnaleźć w tekście. Pomocny jest też indeks owych terminów. Wielką zaletą naszych repetytoriów jest bardzo trafne przewidywanie pytań na tegorocznym egzaminie. W każdej publikacji uwzględniliśmy tegoroczne przewidywania oraz pewniaki na test i egzamin. Repetytorium Gimnazjalisty. Biologia to książka, z którą nauka biologii staje się prosta i przyjemna. Polecamy każdemu gimnazjaliście.','https://skupszop.pl/images/books/9788375173581.jpg');
